INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (28828, 6268, 'Topotecan', 'Bone Marrow Failure Disorders', 'Topotecan induces myelosuppression.  Severe neutropenia (< 500/mm3), severe thrombocytopenia (< 25,000/mm3), and severe anemia (< 8 g/dl) have been reported frequently.  Patients should be instructed to immediately report any signs or symptoms suggesting bone marrow suppression such as fever, sore throat, local infection, or bleeding.   Close clinical monitoring of hematopoietic function is recommended.  Therapy with topotecan should not be initiated in patients with neutrophil counts < 1500/mm3 and/or platelet counts < 100,000/mm3.  Subsequent courses of topotecan should be withheld until neutrophil counts exceed 1000/mm3, platelet counts exceed 100,000/mm3, and/or hemoglobin levels are at least 9 g/dl.', '3', '', 'DDInter', 0);
