INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (28833, 6268, 'Topotecan', 'Kidney Diseases', 'Topotecan is partially eliminated by the kidney.  Approximately 30% of topotecan is excreted in the urine.  Plasma clearance of topotecan was reduced 33% in patients with mild renal impairment (creatinine clearance of 40 to 60 mL/min) and 66% in patients with moderate renal impairment (creatinine clearance of 20 to 40 mL/min).  Therapy with topotecan should be administered cautiously and at a reduced dosage in patients with moderate to severe renal dysfunction.', '3', '', 'DDInter', 0);
