INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (28939, 10208, 'Tranexamic acid', 'Kidney Diseases', 'Tranexamic acid is primarily eliminated by the kidney.  Approximately 95% of tranexamic acid is excreted in the urine unchanged.  The serum concentration of tranexamic acid is increased and the elimination half-life prolonged in patients with renal impairment.  Therapy with tranexamic acid should be administered cautiously and dosages reduced in patients with compromised renal function.  Clinical monitoring of renal  and clotting functions is recommended.', '2', '', 'DDInter', 0);
