INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29136, 481, 'Tramadol', 'Hypotension', 'Tramadol may cause severe hypotension (including orthostatic hypotension, syncope) in ambulatory patients.  Risk is increased in patients whose ability to maintain blood pressure has already been compromised by reduced blood volume or coadministration of certain CNS depressant drugs; these patients should be monitored for signs of hypotension after starting or titrating tramadol.  In patients with circulatory shock, tramadol may cause vasodilation that can further reduce cardiac output and blood pressure; tramadol should be avoided in patients with circulatory shock.', '2', '', 'DDInter', 0);
