INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29188, 481, 'Tramadol', 'Liver Diseases', 'Tramadol is converted by the liver to several metabolites, one of which (referred to as M1) is pharmacologically active and a more potent analgesic than tramadol itself.  The metabolism of both tramadol and M1 has been shown to decrease in patients with advanced cirrhosis of the liver, resulting in increased exposure to tramadol as well as substantially prolonged elimination half-lives for both tramadol and M1.  Therapy with tramadol should be administered cautiously in patients with liver dysfunction.  In patients with severe liver dysfunction, dosage reduction is recommended with the immediate-release formulations, while the extended-release formulations should not be used.', '2', '', 'DDInter', 0);
