INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29334, 4373, 'Trastuzumab emtansine', 'Heart Failure', 'Decreases in left ventricular ejection fraction (LVEF) have been reported with agents that block HER2 activity.  Patients who have received prior anthracyclines, those who received anthracycline after stopping therapy with agents that block HER2 activity, or patients who received prior radiotherapy to the chest area may be at higher risk of decreased LVEF.  Therapy with these agents should be administered cautiously in patients with a previous history of heart conditions.  Evaluate cardiac function before, during, and upon completion of treatment.  Withhold or discontinue therapy with agents that block HER2 activity as appropriate, and for a confirmed clinically significant decrease in left ventricular function, or if the LVEF has not improved or has declined further.  It is recommended to monitor overall cardiac function and LVEF by echocardiogram or MUGA scan as appropriate.', '2', '', 'DDInter', 0);
