INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29342, 4373, 'Trastuzumab emtansine', 'Peripheral Nervous System Diseases', 'Peripheral neuropathy has been reported with the use of agents that block HER2 activity.  Patients experiencing peripheral neuropathy may require temporary interruption, dose reduction, or treatment discontinuation of these agents.  Withhold dosing temporarily for patients experiencing Grade 3 or 4 peripheral neuropathy until resolution.  Close monitoring is recommended when using these agents in patients at risk, and all patients should be monitored on an ongoing basis for signs or symptoms of neurotoxicity.', '2', '', 'DDInter', 0);
