INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29394, 6838, 'Triamcinolone', 'Latent Tuberculosis', 'In patients with latent tuberculosis or tuberculin reactivity, the use of pharmacologic dosages of corticosteroids may cause a reactivation of the disease.  Close monitoring for signs and symptoms of tuberculosis is recommended if corticosteroid therapy is administered to patients with a history of tuberculosis or tuberculin reactivity.  During prolonged corticosteroid therapy, tuberculosis chemoprophylaxis may be considered.', '2', '', 'DDInter', 0);
