INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29464, 6838, 'Triamcinolone', 'Toxic Optic Neuropathy', 'Prolonged use of corticosteroids may cause posterior subcapsular cataracts and elevated intraocular pressure, the latter of which may lead to glaucoma and/or damage to the optic nerves.  Long-term therapy with corticosteroids should be administered cautiously in patients with a history of cataracts, glaucoma, or increased intraocular pressure.', '2', '', 'DDInter', 0);
