INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29479, 6838, 'Triamcinolone', 'Scleroderma, Localized', 'In patients with scleroderma, corticosteroids may precipitate renal crisis with malignant hypertension, possibly via steroid-induced increases in renin substrate and angiotensin II levels and decreases in vasodilator prostaglandin production.  Renal failure may ensue.  Therapy with corticosteroids should be administered cautiously in patients with scleroderma.  In addition, they should be limited to short-term use.', '2', '', 'DDInter', 0);
