INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29512, 11718, 'Trazodone', 'Mania', 'All antidepressants may occasionally cause mania or hypomania, particularly in patients with bipolar disorder.  Therapy with antidepressants should be administered cautiously in patients with a history of mania/hypomania.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29513, 11719, 'Trazodone', 'Mania', 'All antidepressants may occasionally cause mania or hypomania, particularly in patients with bipolar disorder.  Therapy with antidepressants should be administered cautiously in patients with a history of mania/hypomania.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29514, 11718, 'Trazodone', 'Seizures', 'The use of most antidepressants is associated with a risk of seizures.  There have been only rare reports of convulsions, including grand mal seizures, following the administration of nefazodone or trazodone.  Although a causal relationship has not been established, therapy with these agents should be administered cautiously in patients with a history of seizures.', '2', '', 'DDInter', 0);
