INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29591, 11729, 'Trihexyphenidyl', 'Urinary Bladder Neck Obstruction', 'In general, the use of anticholinergic agents is contraindicated in patients with urinary retention and bladder neck obstruction caused by prostatic hypertrophy.  Dysuria may occur and may require catheterization.  Also, anticholinergic drugs may aggravate partial obstructive uropathy.  Caution is advised even when using agents with mild to moderate anticholinergic activity, particularly in elderly patients.', '3', '', 'DDInter', 0);
