INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29701, 28577, 'Trospium', 'Kidney Diseases', 'Trospium is known to be substantially excreted by the kidney, and the risk of adverse reactions may be greater in patients with impaired renal function.  The use of trospium is not recommended in patients with severe renal impairment (creatinine clearance less than 30 mL/minute).  Caution is recommended when using this agent in patients with creatinine clearance ranging from 30 to 80 mL/min as the pharmacokinetics of trospium have not been studied in patients.', '2', '', 'DDInter', 0);
