INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29870, 3998, 'Upadacitinib', 'Peptic Ulcer Perforation', 'Gastrointestinal perforation has been reported with the use of upadacitinib.  This agent should be used with caution in patients who may be at increased risk for gastrointestinal perforation (e.g., patients with a history of diverticulitis or taking NSAIDs).  It is recommended to monitor for new onset abdominal symptoms for early identification of gastrointestinal perforation.', '2', '', 'DDInter', 0);
