INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29872, 3998, 'Upadacitinib', 'Hepatic Insufficiency', 'The use of upadacitinib is not recommended in patients with severe hepatic impairment.  No dose adjustment is required in patients with mild or moderate hepatic impairment.  Treatment with upadacitinib is associated with increased incidence of liver enzyme elevation.  Evaluate baseline liver function and thereafter according to routine patient management.  Treatment should be interrupted if drug-induced liver injury is suspected.', '2', '', 'DDInter', 0);
