INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29899, 15466, 'Urofollitropin', 'Thromboembolism', 'Thromboembolic events, including venous thrombophlebitis, pulmonary embolism, pulmonary infarction, cerebrovascular occlusion (stroke), and arterial occlusion resulting in loss of limb, have been reported during administration of gonadotropin therapy, both in association with and independent of the Ovarian Hyperstimulation Syndrome.  Therapy with gonadotropins should be administered cautiously to patients with or predisposed to thromboembolism.', '3', '', 'DDInter', 0);
