INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29917, 27493, 'Ustekinumab', 'Neoplasms', 'In clinical trials malignancies have been observed in patients receiving ustekinumab.  Patients should be monitored for non-melanoma skin cancers (NMSCs), particularly those patients who have had prior prolonged phototherapy treatment.  Periodic skin examination is recommended for all patients, particularly those with risk factors for skin cancer.  Caution should be exercised when considering ustekinumab for the treatment of patients with a history of malignancy, those who have a known malignancy, or in continuing treatment in patients who develop malignancy while receiving this agent.', '3', '', 'DDInter', 0);
