INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29920, 27493, 'Ustekinumab', 'Posterior Leukoencephalopathy Syndrome', 'The use of ustekinumab may cause posterior leukoencephalopathy syndrome.  One case of reversible posterior leukoencephalopathy syndrome (RPLS) was observed in clinical studies of psoriasis and psoriatic arthritis.  No cases of RPLS were observed in clinical studies of Crohn''s disease.  Conditions with which it has been associated include preeclampsia, eclampsia, acute hypertension, cytotoxic agents and immunosuppressive therapy.  Caution and close monitoring is advised when prescribing this agent to patients at risk of RPLS.  It is recommended to administer appropriate treatment and discontinue therapy if RPLS is suspected.', '3', '', 'DDInter', 0);
