INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29952, 1513, 'Ursodeoxycholic acid', 'Hepatic Insufficiency', 'Ursodiol therapy has not been associated with liver damage.  However, patients given ursodiol should have liver function tests and bilirubin levels measured at the initiation of therapy and every month for three months after start of therapy, and every six months thereafter as appropriate.  Close monitoring should be exercised in patients with liver impairment.  Treatment discontinuation should be considered if the above parameters increase to a level considered clinically significant.', '2', '', 'DDInter', 0);
