INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (30001, 26703, 'Valproic acid', 'Depressive Disorder', 'Antiepileptic drugs can increase depression and suicidal thoughts or behaviors in patients receiving these drugs for any indication.  Patients should be monitored for the emergence or worsening of depression, suicidal thoughts and unusual changes in mood or behavior.  Caregivers and family should be alert for the emergence or worsening of symptoms.  Behaviors of concern should be reported immediately to the healthcare providers.', '3', '', 'DDInter', 0);
