INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (30181, 2066, 'Verapamil', 'Wolff-Parkinson-White Syndrome', 'The use of verapamil is contraindicated for the management of atrial flutter or fibrillation in patients with an accessory AV tract (e.g., those with Wolff-Parkinson-White or Lown-Ganong-Levine syndrome).  Intravenous verapamil has been reported to cause ventricular fibrillation and cardiac arrest in such patients, the mechanism of which is related to the drug''s ability to shorten the refractory period and accelerate antegrade conduction within the accessory pathway.  Although these events have not been associated with chronic use of oral verapamil, a similar risk may exist.', '3', '', 'DDInter', 0);
