INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (30215, 2066, 'Verapamil', 'Cardiomyopathy, Hypertrophic', 'The use of verapamil in patients with hypertrophic cardiomyopathy, or idiopathic hypertrophic subaortic stenosis, has been associated with serious side effects such as pulmonary edema, severe hypotension, sinus bradycardia, AV block, sinus arrest, and death.  However, a causal relationship has not been established.  Therapy with verapamil should be administered cautiously in patients with hypertrophic cardiomyopathy.  Dosage reduction or drug discontinuation may be required if severe adverse effects occur.', '3', '', 'DDInter', 0);
