INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (30232, 2066, 'Verapamil', 'Neuromuscular Junction Diseases', 'Verapamil has been reported to decrease neuromuscular transmission in patients with Duchenne''s muscular dystrophy and to prolong recovery from the neuromuscular blocking agent, vecuronium.  Therapy with verapamil should be administered cautiously in patients with attenuated neuromuscular transmission or myopathy, since these conditions may be exacerbated.  A reduced dosage may be appropriate.', '2', '', 'DDInter', 0);
