INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (30249, 2066, 'Verapamil', 'Kidney Diseases', 'Approximately 70% of an administered dose of verapamil is excreted as metabolites in the urine.  The primary metabolite, norverapamil, has about 20% the cardiovascular activity and can reach steady-state plasma concentrations approaching those of the parent drug.  Patients with impaired renal function may be at greater risk for adverse effects due to drug and metabolite accumulation.  Therapy with verapamil should be administered cautiously in such patients, with appropriate monitoring for excessive pharmacologic effects (e.g., abnormal prolongation of PR interval) and the dosage adjusted accordingly as necessary.', '2', '', 'DDInter', 0);
