INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (30283, 2471, 'Varenicline', 'Mental Disorders', 'Serious neuropsychiatric events including, but not limited to, depression, suicidal ideation, suicide attempt, and completed suicide have been reported in patients taking varenicline.  Caution is recommended and patients should be observed for neuropsychiatric symptoms including changes in behavior, hostility, agitation, depressed mood, and suicide-related events, including ideation, behavior, and attempted suicide.', '3', '', 'DDInter', 0);
