INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (30285, 2471, 'Varenicline', 'Alcoholism', 'Varenicline can enhance the response to alcohol and patients may experience increased intoxicating effects.  In patients who may use alcohol excessively, it should be kept in mind that the potentiation may increase the danger associated with the use of alcohol.  Advise patients to reduce the amount of alcohol they consume and to use caution driving or operating machinery or engaging in other potentially hazardous activities while taking varenicline until they know how this agent may affect them.', '2', '', 'DDInter', 0);
