INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (30475, 209, 'Valsartan', 'Liver Diseases', 'Valsartan is primarily eliminated by biliary excretion, and a minority is excreted in the urine.  Dosage adjustments are not necessary in patients with renal impairment unless they are also volume-depleted, in which case therapy should be initiated under medical supervision.  Likewise, patients with mild to moderate hepatic impairment or biliary obstruction generally do not require a dosage adjustment.  The manufacturer recommends administering valsartan therapy with caution in patients with impaired renal and/or liver function, particularly if these conditions are severe.', '2', '', 'DDInter', 0);
