INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31129, 4005, 'Venlafaxine', 'Weight Loss', 'The use of venlafaxine may occasionally cause significant, dose-dependent weight loss, which may be undesirable in patients suffering from anorexia, malnutrition or excessive weight loss.  The loss of at least 5% of body weight occurred in 6% to 7% of treated patients in premarketing studies, compared to 1% to 2% for placebo.  Anorexia may occur in approximately 10% of patients.  Weight change should be monitored during therapy if venlafaxine is used in malnourished or underweight patients.', '1', '', 'DDInter', 0);
