INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31159, 28911, 'Valganciclovir', 'Neutropenia', 'Severe leukopenia, neutropenia, anemia, thrombocytopenia, pancytopenia, bone marrow aplasia, and aplastic anemia have been reported in patients treated with valganciclovir.  The use of this agent should be avoided if the absolute neutrophil count is less than 500 cells/µL, the platelet count is less than 25,000/µ/L, or the hemoglobin is less than 8 g/dL.  Complete blood counts with differential and platelet counts should be performed frequently, especially in patients that developed leukopenia after using a nucleoside analog, or in whom neutrophil counts are less than 1000 cells/µL at the beginning of treatment.  Therapy with this agent should be administered with caution in patients with bone marrow depression.  Increased monitoring is recommended.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31160, 28911, 'Valganciclovir', 'Kidney Diseases', 'Acute renal failure has been documented in elderly patients (with or without reduced renal function), patients who receive concomitant nephrotoxic drugs, or inadequately hydrated patients.  Adequate hydration, dose reduction and close monitoring of renal function are recommended when using valganciclovir in these patients.  The tablet form of valganciclovir should not be used in adult patients on hemodialysis (CrCl less than 10 mL/min).', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31161, 28911, 'Valganciclovir', 'Hepatic Insufficiency', 'Monitoring is recommended when using valganciclovir in patients with hepatic impairment as the safety and efficacy of this agent has not been studied in these patients.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31162, 29091, 'Venetoclax', 'Hepatic Insufficiency', 'In vitro studies demonstrated that venetoclax is predominantly metabolized in the liver by CYP450 3A4/5.  Based on pharmacokinetic analysis with subjects with mild and moderate hepatic impairment, the venetoclax exposures were similar to those with normal hepatic function; therefore, no dose adjustment is recommended in patients with mild or moderate hepatic impairment.  Based on the drug metabolism, an increase in adverse events might result in patients with moderate to severe hepatic impairment.  It is recommended to monitor patients with moderate to severe hepatic impairment carefully for signs of toxicity during the initiation and the weekly dose ramp-up phase.  Caution is advised when using this agent in patients with severe hepatic impairment as it pharmacokinetics has not been studied.', '2', '', 'DDInter', 0);
