INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31220, 12071, 'Vancomycin', 'Neutropenia', 'Reversible neutropenia has been reported with intravenous use of vancomycin, usually starting one week or more after initiation of therapy or after a total dosage of more than 25 g.  Thrombocytopenia and agranulocytosis (granulocytes < 500/mm3) has occurred rarely.  Patients with preexisting neutropenia should be monitored closely during vancomycin therapy for further decreases in leukocyte counts, and therapy discontinued if appropriate.', '2', '', 'DDInter', 0);
