INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31269, 12309, 'Vincristine', 'Liver Diseases', 'Vincristine is primarily metabolized by the liver.  The influence of severe hepatic impairment on the safety and efficacy of vincristine has not been evaluated.  Additionally, fatal liver toxicity and elevated levels of aspartate aminotransferase have occurred in clinical trials.  Vincristine should be reduced or interrupted if there is evidence of hepatic toxicity.  Therapy with vincristine should be administered cautiously and dosages reduced in patients with compromised hepatic function.  Close clinical monitoring of hepatic function is recommended.', '2', '', 'DDInter', 0);
