INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31279, 12309, 'Vincristine', 'Bone Marrow Failure Disorders', 'Vincristine induces dose-related myelosuppression.  Leukopenia, thrombocytopenia, and anemia have been reported, however vincristine does not appear to have a consistent or significant effect on platelets or red blood cells.  Patients should be instructed to immediately report any signs or symptoms suggesting bone marrow suppression such as fever, sore throat, local infection, or bleeding.  Therapy with vincristine should be administered cautiously in patients with compromised bone marrow reserve.  Monitor complete blood counts prior to each dose.  If Grade 3 or 4 neutropenia, thrombocytopenia, or anemia develops, consider a dose modification or reduction as well as supportive care measures.', '2', '', 'DDInter', 0);
