INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31294, 17032, 'Vinblastine', 'Infections', 'The use of vinblastine is contraindicated in patients with known infectious diseases.  Vinblastine can induce myelosuppression.  All patients should be instructed to immediately report any signs or symptoms suggesting infection such as fever, sore throat, or local infection during therapy with vinblastine.  Close clinical monitoring of hematopoietic function is recommended.  Vinblastine should not be used in patients with neutrophil counts below 1000/mm3.', '3', '', 'DDInter', 0);
