INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31296, 17032, 'Vinblastine', 'Bone Marrow Failure Disorders', 'The use of vinblastine is contraindicated in patients with significant granulocytopenia.  Vinblastine induces dose-dependent leukopenia.  Thrombocytopenia and anemia have been reported infrequently during vinblastine therapy.  Therapy with vinblastine should be administered cautiously in patients whose bone marrow reserve may be severely depressed by prior chemotherapy or irradiation or whose marrow function is recovering from previous cytotoxic therapy.  Patients should be instructed to immediately report any signs or symptoms suggesting bone marrow suppression such as fever, sore throat, local infection, or bleeding.  Close clinical monitoring of hematopoietic function is recommended.', '3', '', 'DDInter', 0);
