INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31388, 23315, 'Vinorelbine', 'Neuromuscular Diseases', 'Sensory and motor neuropathies, including severe neuropathies, occur in patients receiving vinorelbine.  Monitor patients for new or worsening signs and symptoms of neuropathy, such as paresthesia, hyperesthesia, hyporeflexia and muscle weakness while receiving vinorelbine.  Discontinue vinorelbine for CTCAE Grade 2 or greater neuropathy.  Therapy with vinorelbine should be administered cautiously in patients with or predisposed to neuropathy.', '2', '', 'DDInter', 0);
