INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31401, 1172, 'Vitamin A', 'Malabsorption Syndromes', 'Absorption of vitamin A  depends on the presence of bile and absorbable fat in the GI tract.  Prolonged fat malabsorption (cystic fibrosis, hepatic cirrhosis, sprue) or malabsorption syndromes (celiac disease, GI resection) can decrease the absorption of Vitamin A.  Water- miscible vitamin A formulations may be an appropriate alternative in malabsorption conditions for certain conditions requiring vitamin A therapy.', '2', '', 'DDInter', 0);
