INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31463, 1699, 'Von Willebrand Factor Human', 'Thromboembolism', 'Thromboembolic reactions including disseminated intravascular coagulation (DIC), venous thrombosis, pulmonary embolism, myocardial infarction, and stroke can occur after therapy with von Willebrand factor, particularly in patients with risk factors for thrombosis.  Monitor for early signs and symptoms of thrombosis such as pain, swelling, discoloration, dyspnea, cough, hemoptysis, and syncope and have prophylaxis measures against thromboembolism instituted according to current recommendations.', '2', '', 'DDInter', 0);
