INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31465, 7018, 'Vitamin E', 'Liver Diseases', 'Vitamin E is fat soluble and many oral formulations require bile for adequate intestinal absorption.  Hepatic and/or biliary dysfunction decrease the absorption of vitamin E.  Water- miscible vitamin E formulations may be better absorbed from the GI tract under these conditions.  Vitamin E is metabolized by the liver and primarily excreted in bile and feces.  Hepatic and/or biliary impairment can alter the metabolic and therapeutic activity of vitamin E.', '3', '', 'DDInter', 0);
