INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31603, 10558, 'Zaleplon', 'Respiratory Insufficiency', 'Oral anxiolytic, sedative, and hypnotic agents may cause respiratory depression and apnea when given in high dosages or following acute overdose.  However, some patients may be susceptible at commonly used dosages, including the elderly, debilitated or severely ill patients, those receiving other CNS depressants, and those with limited ventilatory reserve, chronic pulmonary insufficiency or other respiratory disorders.  Therapy with anxiolytic, sedative, and hypnotic agents should be administered cautiously in these patients.  Appropriate monitoring and individualization of dosage are recommended.', '2', '', 'DDInter', 0);
