INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31608, 10558, 'Zaleplon', 'Kidney Diseases', 'Zaleplon is excreted in the urine almost exclusively as metabolites and their glucuronides.  The pharmacokinetics of zaleplon are not altered in patients with renal insufficiency.  According to the manufacturer, dosage adjustments are not necessary in patients with mild to moderate renal impairment.  However, caution may be appropriate if the drug is used in patients with severe renal impairment, since data establishing its safety and efficacy are lacking in such patients.', '2', '', 'DDInter', 0);
