INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31652, 7733, 'Warfarin', 'Liver Diseases', 'Oral anticoagulants (coumarin and indandione derivatives) are primarily metabolized by the liver.  Patients with hepatic impairment may have a heightened response to these agents due to decreased clearance of the drugs as well as defective hemostasis associated with impaired synthesis of clotting factors by the liver.  Therapy with oral anticoagulants should be administered cautiously in patients with severe or moderate liver disease.  The INR should be monitored closely, and patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools', '3', '', 'DDInter', 0);
