INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31701, 22288, 'Warfarin', 'Coumarin Sensitivity', 'Patients with a collagen vascular disease (e.g., systemic lupus erythematosus, rheumatoid arthritis, scleroderma), congestive heart failure (especially decompensated disease), severe or prolonged diarrhea, fever, hyperthyroidism, malabsorption, or steatorrhea may exhibit greater than expected hypoprothrombinemic response to oral anticoagulants.  Thus, more frequent laboratory (PT/INR) monitoring and dosage adjustment of anticoagulant may be required based on changes in the patient''s condition.  Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', '2', '', 'DDInter', 0);
