INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31901, 10668, 'Zolmitriptan', 'Liver Diseases', 'Zolmitriptan is primarily metabolized by the liver.  Following oral administration in patients with severe hepatic impairment, plasma concentrations of zolmitriptan were significantly increased (up to several-fold) compared to healthy controls.  Some patients experienced 20 to 80 mm Hg elevations in systolic and/or diastolic blood pressure after a 10 mg dose, presumably due to drug accumulation.  Therapy with zolmitriptan should be administered cautiously in patients with impaired hepatic function.  Lower dosages, no more than 2.5 mg, as well as blood pressure monitoring should be considered in patients with moderate or severe hepatic impairment.', '3', '', 'DDInter', 0);
