INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31905, 525, 'Zolmitriptan', 'Kidney Diseases', 'Zolmitriptan is excreted in the urine primarily as metabolites but also as unchanged drug.  Following oral administration in patients with renal impairment, the clearance of zolmitriptan was not significantly altered in patients with moderate impairment (CrCl = 26 to 50 mL/min) but was reduced by 25% in patients with severe impairment (CrCl = 5 to 25 mL/min) compared to healthy controls.  Therapy with rizatriptan should be administered cautiously in patients with significantly impaired renal function.  A lower initial dosage may be appropriate.', '2', '', 'DDInter', 0);
