INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (32320, 1485, 'Betaxolol', 'Kidney Diseases', 'Betaxolol and its metabolites are eliminated by the kidney.  Patients with renal impairment may be at greater risk for adverse effects from betaxolol due to decreased drug clearance.  Therapy with betaxolol should be administered cautiously in patients with renal impairment.  Dosage adjustments are recommended for patients with severe renal dysfunction (CrCl < 10 mL/min) or undergoing dialysis.', '3', '', 'DDInter', 0);
