INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (32400, 1485, 'Betaxolol', 'Tachycardia', 'Beta-adrenergic blockade in patients with Wolff-Parkinson-White syndrome and tachycardia has been associated with severe bradycardia requiring treatment with a pacemaker.  In one case, this result was reported after an initial dose of 5 mg propranolol.  The use of beta-adrenergic receptor blocking agents (aka beta-blockers) should be administered cautiously in these patients.', '2', '', 'DDInter', 0);
