INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (32434, 16770, 'Zonisamide', 'Liver Failure', 'The use of carbonic anhydrase inhibitors is contraindicated in patients with marked liver disease or cirrhosis.  Carbonic anhydrase inhibitors increase the risk of developing hepatic encephalopathy in these patients.  Extreme caution should be exercised if carbonic anhydrase inhibitors are administered in patients with mild to moderate liver disease as the clearance of the drug can be decreased.  A dose reduction might be needed and monitoring of the liver function is recommended.', '3', '', 'DDInter', 0);
