INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (32492, 12802, 'Ziprasidone', 'Alcoholic Intoxication', 'The use of neuroleptic agents is contraindicated in patients with acute alcohol intoxication exhibiting depressed vital signs.  The central nervous system depressant effects of neuroleptic agents may be additive with those of alcohol.  Severe respiratory depression and respiratory arrest may occur.  Therapy with neuroleptic agents should be administered cautiously in patients who might be prone to acute alcohol intake.', '3', '', 'DDInter', 0);
