INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (32501, 29963, 'Ziprasidone', 'Pneumonia, Aspiration', 'Esophageal dysmotility and aspiration have been associated with the use of antipsychotic drugs.  These drugs should be administered cautiously in patients at risk for aspiration pneumonia.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (32502, 12802, 'Ziprasidone', 'Seizures', 'Antipsychotic and neuroleptic drugs can lower the seizure threshold and trigger seizures in a dose-dependent manner.  This risk is greatest in patients with a history of seizures or with conditions that lower the seizure threshold.  Therapy with these drugs should be administered cautiously in patients with a history of seizures or other predisposing factors, such as head trauma, CNS abnormalities, and alcoholism.', '2', '', 'DDInter', 0);
