INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (32510, 12802, 'Ziprasidone', 'Dyslipidemias', 'Atypical antipsychotic drugs have been associated with undesirable alterations in lipid levels.  While all agents in the class have been shown to produce some changes, each drug has its own specific risk profile.  Before or soon after initiation of antipsychotic medications, a fasting lipid profile should be obtained at baseline and monitored periodically during treatment.', '2', '', 'DDInter', 0);
